mapper.insert:
INSERT INTO Make (
	m_num,       m_status,     m_manager,    m_sdate,
	m_due_date,
	m_note 
)
VALUES (
	#{m_num},    #{m_status},  #{m_manager}, #{m_sdate},
	#{m_due_date, jdbcType=VARCHAR},
	#{m_note, jdbcType=VARCHAR}
)

mapper.update:
UPDATE make SET m_status 	= #{m_status},
				m_manager 	= #{m_manager,      jdbcType=VARCHAR},
				m_sdate 	= #{m_sdate},
				m_due_date 	= #{m_due_date,     jdbcType=VARCHAR}
WHERE m_num = #{m_num}
