mapper.select:
SELECT * FROM (SELECT rownum rn, a.* FROM (SELECT p.p_name rnpc_p_name, p.p_itemcode rnpc_itemcode, bd.bd_cnt rnpc_cnt, b.buy_manager rnpc_manager, to_char(b.buy_wdate, 'yyyy-MM-dd') rnpc_regdate, CASE WHEN b.buy_status = 0 THEN '구매진행' WHEN b.buy_status = 2 THEN '구매완료' WHEN b.buy_status = 5 THEN '수불완료' END AS rnpc_status FROM buying b, buying_detail bd, product p WHERE b.buy_date = bd.buy_date AND b.cust_no = bd.cust_no AND bd.p_itemcode = p.p_itemcode AND b.buy_date = #{rnpc_date}
AND b.buy_status = 0
AND b.buy_status = 2
AND b.buy_status = 5
ORDER BY b.buy_status DESC, p.p_itemcode ) a ) WHERE rn BETWEEN #{start} and #{end}

SELECT * FROM (SELECT rownum rn, a.* FROM (SELECT p.p_name rnpc_p_name, p.p_itemcode rnpc_itemcode, sd.sd_cnt rnpc_cnt, s.s_manager rnpc_manager, to_char(s.s_wdate, 'yyyy-MM-dd') rnpc_regdate, CASE WHEN s.s_status = 0 THEN '판매진행' WHEN s.s_status = 4 THEN '판매완료' WHEN s.s_status = 5 THEN '수불완료' END AS rnpc_status FROM sale s, sale_detail sd, product p WHERE s.s_date = sd.s_date AND s.cust_no = sd.cust_no AND sd.p_itemcode = p.p_itemcode AND s.s_date = #{rnpc_date}
AND s.s_status = 0
AND s.s_status = 4
AND s.s_status = 5
ORDER BY s.s_status DESC, p.p_itemcode ) a ) WHERE rn BETWEEN #{start} and #{end}

SELECT * FROM (SELECT rownum rn, a.*, to_char(a.rnpc_date, 'yyyy-MM-dd') rnpc_regdate FROM (SELECT p.p_name rnpc_p_name, p.p_itemcode rnpc_itemcode, md.md_pro_quantity rnpc_cnt, m.m_manager rnpc_manager, to_date(md.m_num, 'yyyy-MM-dd') rnpc_date, CASE WHEN md.md_status = 0 THEN '생산진행' WHEN md.md_status = 4 THEN '생산완료' WHEN md.md_status = 5 THEN '수불완료' END AS rnpc_status FROM make m, make_detail md, factory f, product p WHERE m.m_num = md.m_num AND md.f_id = f.f_id AND md.f_id = p.f_id AND md.p_itemcode = p.p_itemcode AND md.m_num = #{rnpc_date}
AND md.md_status = 0
AND md.md_status = 4
AND md.md_status = 5
ORDER BY md.md_status DESC, p.p_itemcode ) a ) WHERE rn BETWEEN #{start} and #{end}

SELECT * FROM (SELECT rownum rn, a.* FROM (SELECT p.p_name rnpc_p_name, p.p_itemcode rnpc_itemcode, sd.sd_cnt rnpc_cnt, s.s_manager rnpc_manager, to_char(s.s_wdate, 'yyyy-MM-dd') rnpc_regdate, CASE WHEN s.s_status = 0 THEN '판매진행' WHEN s.s_status = 4 THEN '판매완료' WHEN s.s_status = 5 THEN '수불완료' END AS rnpc_status FROM sale s, sale_detail sd, product p WHERE s.s_date = sd.s_date AND s.cust_no = sd.cust_no AND sd.p_itemcode = p.p_itemcode AND s.s_date = #{rnpc_date}
AND s.s_status = 0
AND s.s_status = 4
AND s.s_status = 5
ORDER BY s.s_status DESC, p.p_itemcode ) a ) WHERE rn BETWEEN #{start} and #{end}
